def vowels_count(s):
    """
    Returns the number of vowels in the given string.
    Vowels are 'a', 'e', 'i', 'o', 'u'. 'y' is a vowel only if it is at the end of the word.
    """
    vowels = set('aeiou')
    count = 0
    for i, char in enumerate(s.lower()):
        if char in vowels:
            count += 1
        elif char == 'y' and i == len(s) - 1:
            count += 1
    return count

